#include <iostream>
using namespace std;

// Function to find the nth Fibonacci number using recursion
int fibonacci(int n) {
    // Base case: if n is 0 or 1, return n as it is the Fibonacci number
    if ( (n == 0 || n == 1) {
        return n;
    }
    // Recursive case: return the sum of the two preceding Fibonacci numbers
    return fibonacci(n - 1) + fibonacci(n - 2);
}

